["custom"] = "function()\n    if GetTime() - aura_env.UpdateCheckARU > 0.5 then\n        aura_env.UpdateCheckARU = GetTime()\n        aura_env.count = 0\n        aura_env.members = 0\n        aura_env.deadMembers = 0    \n        local _,_,_, instanceID = UnitPosition(\"player\")\n        local _, _, playerClass = UnitClass(\"player\")\n        local found = 0\n        local glow = false\n        \n        for member in WA_IterateGroupMembers() do\n            aura_env.members = aura_env.members + 1\n            if member then\n                local _, _, class = UnitClass(member)\n                local _,_,_, instanceIDMember = UnitPosition(member)\n                \n                \n                local dead = false\n                if UnitIsDeadOrGhost(member) or not UnitIsConnected(member) or instanceID ~= instanceIDMember then\n                    aura_env.deadMembers = aura_env.deadMembers + 1\n                    dead = true\n                else\n                    if class == 1 then\n                        found = 1\n                    end\n                end\n                \n                if dead == false and WA_GetUnitBuff(member, 6673) then\n                    aura_env.count = aura_env.count + 1\n                else\n                    if aura_env.config[\"option2\"] and playerClass == 1 and (member == \"player\" or UnitInRange(member)) then\n                        glow = true\n                    end\n                end\n            end\n        end\n        aura_env.region:SetGlow(glow)\n        if found == 0 or aura_env.count >= aura_env.members - aura_env.deadMembers then\n            aura_env.val = false\n            return aura_env.val\n        end\n        aura_env.val = true\n    end\n    return aura_env.val\nend",
